import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Text } from '../Language/Language';
import Navbar from '../Navbar'

function Login() {
    const [signin, setSignin] = useState({
        loginEmail: '',
        loginPassword: ''
    })

    function hanleEmail(e) {
        setSignin({loginEmail: e.target.value})
        console.log(e.target.value)
    }

    function hanlePassword(e) {
        setSignin({loginPassword: e.target.value})
    }

    useEffect(() => {
        // POST request using fetch inside useEffect React hook
        const handleSubmitLogin = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 
                email: this.state.loginEmail,
                password: this.state.loginPassword
             })
        };
        fetch('https://jsonplaceholder.typicode.com/posts', handleSubmitLogin)
            .then(response => response.json())
            .then(data => setPostId(data.id));

    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    }, []);

    return (
        <div>
            <Navbar />
            <form>
                <input 
                    type='email'
                    placeholder='Email'
                    name='email'
                    onChange={hanleEmail}
                    />
                    <br/>
                <input 
                    type='password'
                    placeholder='Password'
                    name='password'
                    onChange={hanlePassword}
                    />
                    <br/>
                <button type='submit'>Submit</button>    
            </form>
            <p>Don't have account?</p>
            <Link to='/register'><Text t='register'/></Link>
        </div>
    );
}

export default Login;
