import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Text } from '../Language/Language';
import Navbar from '../Navbar'

function Login() {
    const [signin, setSignin] = useState({
        loginEmail: '',
        loginPassword: ''
    });

    function hanleEmail(e) {
        setSignin({loginEmail: e.target.value})
        console.log(e.target.value)
    };

    function hanlePassword(e) {
        setSignin({loginPassword: e.target.value})
    };

    // useEffect(() => {
    //     // POST request using fetch inside useEffect React hook
    //     const handleSubmitLogin = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({ 
    //             email: signin.loginEmail,
    //             password: signin.loginPassword
    //          })
    //     };
    //     fetch('http://localhost:3000/', handleSubmitLogin)
    //         .then(response => response.json())
    //         .then(console.log())
    //         .then(user => {
    //             if(user.id) {
    //                 this.props.loadUser(user)
    //             }
    //         });

    // // empty dependency array means this effect will only run once (like componentDidMount in classes)
    // }, []);

    // componentDidMount() {
    //     fetch('http://localhost:3000')
    //         .then(response => response.json())
    //         .then(console.log())
    // } 
    componentDidMount=() => {
        // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title: 'React POST Request Example' })
        };
        fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));
    }  

    return (
        <div>
            <Navbar />
            <form>
                <input 
                    type='email'
                    placeholder='Email'
                    name='email'
                    onChange={hanleEmail}
                    />
                    <br/>
                <input 
                    type='password'
                    placeholder='Password'
                    name='password'
                    onChange={hanlePassword}
                    />
                    <br/>
                <button type='submit'>Submit</button>    
            </form>
            <p>Don't have account?</p>
            <Link to='/register'><Text t='register'/></Link>
        </div>
    );
}

export default Login;
