import React from 'react';

function Steps({ingridients, steps, title, time}) {
    function timeStep() {
        if(time) {
            return (<p>Time for step: {time}</p>)
        } else {
            return null
        }
    }


    return (
        <div>
            <h4>{title}</h4>
           <div>{timeStep()}</div>
            <ul>
                {ingridients.map(ingridient => (
                    <li>{ingridient.text}</li>
                ))}
            </ul>
            <ol>
                {steps.map(st => (
                    <li>{st[1]}</li>
                ))}
            </ol>
        </div>
    );
}

export default Steps;
