    const [search, setSearch] = useState('');
    // const [query, setQuery] = useState('');

    function handleSearch(e) {
        setSearch(e.target.value);
    }

    // function submitSearch(e) {
    //     // e.preventDefault();
    //     setQuery(search);
    // }


    useEffect(() => {
        fetch('https://gastronomia-api.herokuapp.com/recipes')
        .then(res => res.json())
        .then(data => {
            setRecipes(data)
        })
    },[])

    // console.log(recipes)

    return (
        <div>
            <form>
