import React from 'react';

function Steps({ingridients, steps, title}) {
    return (
        <div>
            <h4>{title}</h4>
            <ul>
                {ingridients.map(ingridient => (
                    <li>{ingridient.text}</li>
                ))}
            </ul>
            <ul>
                {steps.map(st => (
                    <li>{st.text}</li>
                ))}
            </ul>
        </div>
    );
}

export default Steps;
