import React from 'react';

function Steps({ingridients, steps, title, time}) {
    function timeStep() {
        if(time) {
            return (<p>{time}</p>)
        }
    }


    return (
        <div>
            <h4>{title}</h4>

            <ul>
                {ingridients.map(ingridient => (
                    <li>{ingridient.[1]}</li>
                ))}
            </ul>
            <ul>
                {steps.map(st => (
                    <li>{st.text}</li>
                ))}
            </ul>
        </div>
    );
}

export default Steps;
