import React from 'react';

function Recipe({title, description, time, price, amount, methods}) {
    return (
        <div className='recipe'>
            <h2>{title}</h2>
            <h3>{description}</h3>
            <h5>Time {time}</h5>
            <h5>Amount: {amount}</h5>
            <h5>Price: {price}</h5>
            <ol>
                {methods.map(step => (
                    <li>{step.steps}</li>
                ))}

            </ol>
        </div>
    );
}

export default Recipe;
