import React, { useState, useEffect } from 'react';

import Recipe from './Recipe';

function Api({match}) {
    const [recipe, setRecipe] = useState({});
    useEffect(() => {
        fetch(`https://gastronomia-api.herokuapp.com/recipes/${[match.params.id]}`)
        .then(res => res.json())
        .then(data => {
            console.log(data)
            console.log(match)
            setRecipe(data)
        })
    }, [])


    return (
        <div>

                title={recip.title}
                description={recip.description}
                time={recip.time}
                price={recip.price}
                amount={recip.amount}
                key={recip.id}
                methods={recip.methods}

        </div>
    );
}

export default Api;
