        .then(res => res.json())
        .then(data => {
            console.log(data)
            console.log(match)
            setRecipe(data)
        })
    }, [])
    const [recipe, setRecipe] = useState({});

    return (
        <div>
            <div>{recipe.map(recip => (
            <Recipe 
                title={recip.title}
                description={recip.description}
                time={recip.time}
                price={recip.price}
                amount={recip.amount}
                key={recip.id}
                methods={recip.methods}
                />
            ))}</div>
        </div>
    );
}

