import React, { useState, useEffect } from 'react';

import Recipe from './Recipe';

function Api({match}) {
    const [recipe, setRecipe] = useState({});
    useEffect(() => {
        fetch(`https://gastronomia-api.herokuapp.com/recipes/${[match.params.id]}`)
        .then(res => res.json())
        .then(data => {
            console.log(data)
            console.log(match)
            setRecipe(data)
        })
    }, [])


    return (
        <div>
           <h3>{recipe.title}</h3>
           <h5>{recipe.description}</h5>
           <div>
               {data.map(step => (
                   <p>{step.title}</p>
               ))}
           </div>
        </div>
    );
}

export default Api;
